// Sample input data
const inputData = [
  {
    "Team Member": "sowmiya",
    "Completed Tasks": "Finished report draft, updated docs",
    "Blocked Tasks": "Waiting for review",
    "Planned Tasks": "Finalize report, send to client",
    "Notes": "Needs quick review"
  },
  {
    "Team Member": "Dharshini",
    "Completed Tasks": "Fixed bug #123, pushed to staging",
    "Blocked Tasks": "API endpoint issues",
    "Planned Tasks": "Test API fix, integrate feature",
    "Notes": "Might need API support"
  },
  {
    "Team Member": "stephy",
    "Completed Tasks": "Onboarded new team member",
    "Blocked Tasks": "None",
    "Planned Tasks": "Prepare training slides",
    "Notes": "Training session tomorrow"
  },
  {
    "Team Member": "karthi",
    "Completed Tasks": "Reviewed PRs, updated deployment scripts",
    "Blocked Tasks": "Awaiting deployment approval",
    "Planned Tasks": "Deploy updates, monitor logs",
    "Notes": "Deployment planned for tomorrow"
  }
];

// Initialize summary object
const summary = {
  Completed: [],
  Blocked: [],
  Planned: [],
  Notes: []
};

// Process each team member's updates
for (const update of inputData) {
  const teamMember = update["Team Member"] || "Unknown";
  const completed = (update["Completed Tasks"] || "").trim();
  const blocked = (update["Blocked Tasks"] || "").trim();
  const planned = (update["Planned Tasks"] || "").trim();
  const notes = (update["Notes"] || "").trim();

  if (completed && completed.toLowerCase() !== "none") {
    summary.Completed.push(`- ${teamMember}: ${completed}`);
  }

  if (blocked && blocked.toLowerCase() !== "none") {
    summary.Blocked.push(`- ${teamMember}: ${blocked}`);
  }

  if (planned && planned.toLowerCase() !== "none") {
    summary.Planned.push(`- ${teamMember}: ${planned}`);
  }

  if (notes && notes.toLowerCase() !== "none") {
    summary.Notes.push(`- ${teamMember}: ${notes}`);
  }
}

// Build the final formatted summary
const todayDate = new Date().toISOString().split("T")[0];
const formattedSummary = 
`Daily Summary for ${todayDate}

Completed:
${summary.Completed.join('\n') || "No updates"}

Blocked:
${summary.Blocked.join('\n') || "No blockers"}

Planned for Tomorrow:
${summary.Planned.join('\n') || "No plans"}

Additional Notes:
${summary.Notes.join('\n') || "No notes"}
`;

// Return formatted summary for n8nâ€™s expected output
return [
  {
    json: {
      summary: formattedSummary
    }
  }
];
